import{_ as l,o as a,c as i,ag as r}from"./chunks/framework.9PtxzSVj.js";const b=JSON.parse('{"title":"世界与频道管理","description":"世界与频道的创建、配置与权限管理","frontmatter":{"title":"世界与频道管理","description":"世界与频道的创建、配置与权限管理"},"headers":[],"relativePath":"admin/world-channel.md","filePath":"admin/world-channel.md","lastUpdated":1770388790000}'),d={name:"admin/world-channel.md"};function o(e,t,h,n,s,u){return a(),i("div",null,[...t[0]||(t[0]=[r('<h1 id="世界与频道管理" tabindex="-1">世界与频道管理 <a class="header-anchor" href="#世界与频道管理" aria-label="Permalink to &quot;世界与频道管理&quot;">​</a></h1><h2 id="世界管理" tabindex="-1">世界管理 <a class="header-anchor" href="#世界管理" aria-label="Permalink to &quot;世界管理&quot;">​</a></h2><h3 id="进入世界设置" tabindex="-1">进入世界设置 <a class="header-anchor" href="#进入世界设置" aria-label="Permalink to &quot;进入世界设置&quot;">​</a></h3><ol><li>进入目标世界</li><li>点击世界名称旁的设置图标</li><li>或直接访问 <code>/world/{id}/settings</code></li></ol><h3 id="世界基本设置" tabindex="-1">世界基本设置 <a class="header-anchor" href="#世界基本设置" aria-label="Permalink to &quot;世界基本设置&quot;">​</a></h3><table tabindex="0"><thead><tr><th>设置</th><th>说明</th></tr></thead><tbody><tr><td>世界名称</td><td>修改世界的显示名称</td></tr><tr><td>世界描述</td><td>修改世界的简介</td></tr><tr><td>世界图标</td><td>上传新的世界图标</td></tr><tr><td>公开状态</td><td>切换公开/私有</td></tr></tbody></table><h3 id="成员管理" tabindex="-1">成员管理 <a class="header-anchor" href="#成员管理" aria-label="Permalink to &quot;成员管理&quot;">​</a></h3><p><strong>查看成员</strong>：</p><ul><li>成员列表显示所有世界成员</li><li>可以查看成员的加入时间、角色等</li></ul><p><strong>添加成员</strong>：</p><ol><li>点击&quot;添加成员&quot;</li><li>搜索用户名</li><li>选择并添加</li></ol><p><strong>移除成员</strong>：</p><ol><li>在成员列表找到目标用户</li><li>点击&quot;移除&quot;</li><li>确认操作</li></ol><p><strong>设置管理员</strong>：</p><ol><li>找到要提升的成员</li><li>点击&quot;设为管理员&quot;</li><li>该成员获得世界管理权限</li></ol><h3 id="邀请管理" tabindex="-1">邀请管理 <a class="header-anchor" href="#邀请管理" aria-label="Permalink to &quot;邀请管理&quot;">​</a></h3><p><strong>生成邀请链接</strong>：</p><ol><li>进入邀请管理</li><li>点击&quot;生成邀请链接&quot;</li><li>可设置： <ul><li>有效期（永久/7天/24小时/1小时）</li><li>最大使用次数</li></ul></li><li>复制生成的链接</li></ol><p><strong>查看邀请记录</strong>：</p><ul><li>查看所有生成的邀请链接</li><li>查看使用情况</li><li>可以撤销未过期的邀请</li></ul><h3 id="转让世界" tabindex="-1">转让世界 <a class="header-anchor" href="#转让世界" aria-label="Permalink to &quot;转让世界&quot;">​</a></h3><ol><li>进入世界设置 → 高级</li><li>点击&quot;转让世界&quot;</li><li>选择新的世界所有者</li><li>确认转让</li></ol><div class="warning custom-block"><p class="custom-block-title">注意</p><p>转让后，你将失去世界所有权，但保留管理员权限。</p></div><h3 id="删除世界" tabindex="-1">删除世界 <a class="header-anchor" href="#删除世界" aria-label="Permalink to &quot;删除世界&quot;">​</a></h3><div class="danger custom-block"><p class="custom-block-title">危险操作</p><p>删除世界会同时删除该世界的所有频道、消息、附件等数据，此操作不可恢复。</p></div><ol><li>进入世界设置 → 高级</li><li>点击&quot;删除世界&quot;</li><li>输入世界名称确认</li><li>确认删除</li></ol><hr><h2 id="频道管理" tabindex="-1">频道管理 <a class="header-anchor" href="#频道管理" aria-label="Permalink to &quot;频道管理&quot;">​</a></h2><h3 id="创建频道" tabindex="-1">创建频道 <a class="header-anchor" href="#创建频道" aria-label="Permalink to &quot;创建频道&quot;">​</a></h3><ol><li>在世界左侧栏点击&quot;+&quot;按钮</li><li>填写频道信息： <ul><li>频道名称</li><li>频道描述</li><li>父级频道（可选，用于创建子频道）</li></ul></li><li>确认创建</li></ol><h3 id="频道设置" tabindex="-1">频道设置 <a class="header-anchor" href="#频道设置" aria-label="Permalink to &quot;频道设置&quot;">​</a></h3><table tabindex="0"><thead><tr><th>设置</th><th>说明</th></tr></thead><tbody><tr><td>基本信息</td><td>名称、描述</td></tr><tr><td>权限设置</td><td>频道级权限覆盖</td></tr><tr><td>身份卡</td><td>管理频道内的身份卡</td></tr><tr><td>骰子宏</td><td>配置该频道的骰子宏</td></tr><tr><td>iForm</td><td>嵌入表单配置</td></tr><tr><td>便签</td><td>频道顶部公告</td></tr></tbody></table><h3 id="邮件提醒设置" tabindex="-1">邮件提醒设置 <a class="header-anchor" href="#邮件提醒设置" aria-label="Permalink to &quot;邮件提醒设置&quot;">​</a></h3><p>如服务器开启邮件通知，频道内可配置邮件提醒：</p><ul><li>绑定接收邮箱与推送延迟</li><li>可选自定义 SMTP（单独用于该频道）</li><li>支持发送测试邮件验证配置</li></ul><h3 id="频道权限" tabindex="-1">频道权限 <a class="header-anchor" href="#频道权限" aria-label="Permalink to &quot;频道权限&quot;">​</a></h3><p>频道权限可以覆盖世界级别的默认权限：</p><table tabindex="0"><thead><tr><th>权限</th><th>说明</th></tr></thead><tbody><tr><td>查看频道</td><td>是否能看到该频道</td></tr><tr><td>发送消息</td><td>是否能发言</td></tr><tr><td>管理消息</td><td>是否能删除他人消息</td></tr><tr><td>管理频道</td><td>是否能编辑频道设置</td></tr></tbody></table><h3 id="删除频道" tabindex="-1">删除频道 <a class="header-anchor" href="#删除频道" aria-label="Permalink to &quot;删除频道&quot;">​</a></h3><ol><li>进入频道设置</li><li>点击&quot;删除频道&quot;</li><li>确认操作</li></ol>',40)])])}const q=l(d,[["render",o]]);export{b as __pageData,q as default};
