import{_ as a,o as n,c as d,ag as r}from"./chunks/framework.9PtxzSVj.js";const c=JSON.parse('{"title":"核心概念","description":"世界、频道、身份卡与权限体系","frontmatter":{"title":"核心概念","description":"世界、频道、身份卡与权限体系"},"headers":[],"relativePath":"guide/concepts.md","filePath":"guide/concepts.md","lastUpdated":1770390275000}'),s={name:"guide/concepts.md"};function e(l,t,i,o,h,p){return n(),d("div",null,[...t[0]||(t[0]=[r(`<h1 id="核心概念" tabindex="-1">核心概念 <a class="header-anchor" href="#核心概念" aria-label="Permalink to &quot;核心概念&quot;">​</a></h1><p>理解 SealChat 的核心概念，帮助你更好地使用和管理平台。</p><hr><h2 id="组织结构" tabindex="-1">组织结构 <a class="header-anchor" href="#组织结构" aria-label="Permalink to &quot;组织结构&quot;">​</a></h2><p>SealChat 采用<strong>三层组织结构</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>世界（World）</span></span>
<span class="line"><span>  └── 频道（Channel）</span></span>
<span class="line"><span>        └── 消息（Message）</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="层级说明" tabindex="-1">层级说明 <a class="header-anchor" href="#层级说明" aria-label="Permalink to &quot;层级说明&quot;">​</a></h3><table tabindex="0"><thead><tr><th>层级</th><th>类比</th><th>用途</th></tr></thead><tbody><tr><td><strong>世界</strong></td><td>Discord 服务器 / Slack 工作区</td><td>最大的组织单位，包含多个频道</td></tr><tr><td><strong>频道</strong></td><td>Discord 频道 / 聊天房间</td><td>特定话题的讨论空间</td></tr><tr><td><strong>消息</strong></td><td>聊天消息</td><td>具体的交流内容</td></tr></tbody></table><h3 id="典型使用场景" tabindex="-1">典型使用场景 <a class="header-anchor" href="#典型使用场景" aria-label="Permalink to &quot;典型使用场景&quot;">​</a></h3><p><strong>跑团团队</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>[跑团世界] ← 世界</span></span>
<span class="line"><span>  ├── 大厅           ← 通用讨论</span></span>
<span class="line"><span>  ├── 角色扮演       ← IC（角色扮演）区</span></span>
<span class="line"><span>  ├── 闲聊           ← OOC（跳出角色）区</span></span>
<span class="line"><span>  ├── 资料区         ← 规则书、设定</span></span>
<span class="line"><span>  └── 骰子测试       ← 测试骰子</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><hr><h2 id="世界-world" tabindex="-1">世界（World） <a class="header-anchor" href="#世界-world" aria-label="Permalink to &quot;世界（World）&quot;">​</a></h2><h3 id="什么是世界" tabindex="-1">什么是世界？ <a class="header-anchor" href="#什么是世界" aria-label="Permalink to &quot;什么是世界？&quot;">​</a></h3><p>世界是 SealChat 的最顶层组织单位。每个世界是一个独立的空间，拥有独立的成员、频道和权限设置。</p><h3 id="世界类型" tabindex="-1">世界类型 <a class="header-anchor" href="#世界类型" aria-label="Permalink to &quot;世界类型&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>说明</th><th>适用场景</th></tr></thead><tbody><tr><td><strong>私有世界</strong></td><td>需要邀请链接才能加入</td><td>跑团、私密团队</td></tr><tr><td><strong>公开世界</strong></td><td>任何人都可以搜索并加入</td><td>开放社区</td></tr></tbody></table><h3 id="世界大厅" tabindex="-1">世界大厅 <a class="header-anchor" href="#世界大厅" aria-label="Permalink to &quot;世界大厅&quot;">​</a></h3><p>公开世界会出现在<strong>世界大厅</strong>中，用户可浏览、搜索并申请加入。</p><h3 id="世界属性" tabindex="-1">世界属性 <a class="header-anchor" href="#世界属性" aria-label="Permalink to &quot;世界属性&quot;">​</a></h3><table tabindex="0"><thead><tr><th>属性</th><th>说明</th></tr></thead><tbody><tr><td>名称</td><td>世界的显示名称</td></tr><tr><td>描述</td><td>世界的简介</td></tr><tr><td>图标</td><td>世界的头像图片</td></tr><tr><td>公开状态</td><td>公开或私有</td></tr><tr><td>创建时间</td><td>世界创建的时间</td></tr><tr><td>成员数</td><td>当前成员数量</td></tr></tbody></table><h3 id="默认大厅" tabindex="-1">默认大厅 <a class="header-anchor" href="#默认大厅" aria-label="Permalink to &quot;默认大厅&quot;">​</a></h3><p>创建世界时，系统会自动创建一个&quot;大厅&quot;频道作为默认入口。</p><h3 id="世界管理" tabindex="-1">世界管理 <a class="header-anchor" href="#世界管理" aria-label="Permalink to &quot;世界管理&quot;">​</a></h3><p>世界创建者默认是世界管理员，可以：</p><ul><li>编辑世界信息</li><li>管理频道</li><li>管理成员权限</li><li>生成邀请链接</li><li>转让管理权</li><li>删除世界</li></ul><h3 id="世界术语库" tabindex="-1">世界术语库 <a class="header-anchor" href="#世界术语库" aria-label="Permalink to &quot;世界术语库&quot;">​</a></h3><p>世界可以维护术语与关键词库，用于在消息中高亮并展示说明，适合规则词条与设定百科管理。</p><hr><h2 id="频道-channel" tabindex="-1">频道（Channel） <a class="header-anchor" href="#频道-channel" aria-label="Permalink to &quot;频道（Channel）&quot;">​</a></h2><h3 id="什么是频道" tabindex="-1">什么是频道？ <a class="header-anchor" href="#什么是频道" aria-label="Permalink to &quot;什么是频道？&quot;">​</a></h3><p>频道是世界内的聊天空间，成员在频道中发送和接收消息。</p><h3 id="频道特性" tabindex="-1">频道特性 <a class="header-anchor" href="#频道特性" aria-label="Permalink to &quot;频道特性&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特性</th><th>说明</th></tr></thead><tbody><tr><td><strong>层级结构</strong></td><td>频道可以嵌套，形成父子关系</td></tr><tr><td><strong>独立权限</strong></td><td>每个频道可以有独立的权限设置</td></tr><tr><td><strong>身份卡绑定</strong></td><td>成员可以在不同频道使用不同身份卡</td></tr><tr><td><strong>专属功能</strong></td><td>骰子宏、iForm 表单等可按频道配置</td></tr></tbody></table><h3 id="频道层级" tabindex="-1">频道层级 <a class="header-anchor" href="#频道层级" aria-label="Permalink to &quot;频道层级&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>[战役主频道]</span></span>
<span class="line"><span>  ├── [第一章]</span></span>
<span class="line"><span>  │     ├── 场景1</span></span>
<span class="line"><span>  │     └── 场景2</span></span>
<span class="line"><span>  ├── [第二章]</span></span>
<span class="line"><span>  └── [休息区]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h3 id="频道设置" tabindex="-1">频道设置 <a class="header-anchor" href="#频道设置" aria-label="Permalink to &quot;频道设置&quot;">​</a></h3><table tabindex="0"><thead><tr><th>设置项</th><th>说明</th></tr></thead><tbody><tr><td>频道名称</td><td>显示名称</td></tr><tr><td>频道描述</td><td>频道的用途说明</td></tr><tr><td>权限设置</td><td>谁可以查看、发言、管理</td></tr><tr><td>骰子宏</td><td>该频道可用的骰子宏</td></tr><tr><td>iForm</td><td>嵌入的表单配置</td></tr><tr><td>便签</td><td>频道顶部的公告内容</td></tr></tbody></table><hr><h2 id="身份卡-identity-card" tabindex="-1">身份卡（Identity Card） <a class="header-anchor" href="#身份卡-identity-card" aria-label="Permalink to &quot;身份卡（Identity Card）&quot;">​</a></h2><h3 id="什么是身份卡" tabindex="-1">什么是身份卡？ <a class="header-anchor" href="#什么是身份卡" aria-label="Permalink to &quot;什么是身份卡？&quot;">​</a></h3><p>身份卡是 SealChat 最具特色的功能之一。它允许用户在频道中以不同的&quot;身份&quot;发言，每个身份卡拥有独立的昵称、头像和描述。</p><h3 id="身份卡用途" tabindex="-1">身份卡用途 <a class="header-anchor" href="#身份卡用途" aria-label="Permalink to &quot;身份卡用途&quot;">​</a></h3><table tabindex="0"><thead><tr><th>场景</th><th>说明</th></tr></thead><tbody><tr><td><strong>角色扮演</strong></td><td>为你扮演的每个角色创建一张身份卡</td></tr><tr><td><strong>主持人模式</strong></td><td>使用专门的&quot;主持人&quot;身份卡发言</td></tr><tr><td><strong>NPC 发言</strong></td><td>主持人可以用 NPC 身份卡模拟 NPC 对话</td></tr><tr><td><strong>匿名发言</strong></td><td>使用匿名身份卡保护隐私</td></tr></tbody></table><h3 id="身份卡属性" tabindex="-1">身份卡属性 <a class="header-anchor" href="#身份卡属性" aria-label="Permalink to &quot;身份卡属性&quot;">​</a></h3><table tabindex="0"><thead><tr><th>属性</th><th>说明</th><th>示例</th></tr></thead><tbody><tr><td>昵称</td><td>身份卡的显示名称</td><td>&quot;艾露莎·月影&quot;</td></tr><tr><td>头像</td><td>身份卡的头像图片</td><td>角色立绘</td></tr><tr><td>描述</td><td>角色的简介或备注</td><td>&quot;精灵法师，擅长冰系魔法&quot;</td></tr><tr><td>权限</td><td>该身份卡的权限范围</td><td>发言权、编辑权</td></tr></tbody></table><h3 id="身份卡管理" tabindex="-1">身份卡管理 <a class="header-anchor" href="#身份卡管理" aria-label="Permalink to &quot;身份卡管理&quot;">​</a></h3><p><strong>创建身份卡</strong>：</p><ol><li>进入频道设置</li><li>选择&quot;身份卡管理&quot;</li><li>点击&quot;创建身份卡&quot;</li><li>填写昵称、上传头像、添加描述</li></ol><p><strong>切换身份卡</strong>：</p><ol><li>在消息输入框旁边找到身份卡选择器</li><li>点击选择要使用的身份卡</li><li>之后发送的消息将以该身份卡身份发出</li></ol><p><strong>身份卡分组</strong>：</p><ul><li>可以创建文件夹对身份卡进行分组</li><li>便于管理大量角色（如主持人的 NPC 库）</li></ul><h3 id="示例-跑团场景" tabindex="-1">示例：跑团场景 <a class="header-anchor" href="#示例-跑团场景" aria-label="Permalink to &quot;示例：跑团场景&quot;">​</a></h3><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>玩家 A 的身份卡：</span></span>
<span class="line"><span>├── [我自己]       ← OOC 发言时使用</span></span>
<span class="line"><span>└── [艾露莎]       ← IC 发言时使用</span></span>
<span class="line"><span></span></span>
<span class="line"><span>主持人的身份卡：</span></span>
<span class="line"><span>├── [主持人]       ← 系统说明、规则解释</span></span>
<span class="line"><span>├── [商人 NPC]     ← 扮演商人</span></span>
<span class="line"><span>├── [酒馆老板]     ← 扮演酒馆老板</span></span>
<span class="line"><span>└── [神秘旅者]     ← 扮演重要 NPC</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><hr><h2 id="权限系统" tabindex="-1">权限系统 <a class="header-anchor" href="#权限系统" aria-label="Permalink to &quot;权限系统&quot;">​</a></h2><h3 id="权限模型" tabindex="-1">权限模型 <a class="header-anchor" href="#权限模型" aria-label="Permalink to &quot;权限模型&quot;">​</a></h3><p>SealChat 采用基于 <strong>RBAC（角色访问控制）</strong> 的细粒度权限系统。</p><h3 id="权限层级" tabindex="-1">权限层级 <a class="header-anchor" href="#权限层级" aria-label="Permalink to &quot;权限层级&quot;">​</a></h3><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>系统级权限</span></span>
<span class="line"><span>  └── 世界级权限</span></span>
<span class="line"><span>        └── 频道级权限</span></span>
<span class="line"><span>              └── 身份卡级权限</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="常见权限" tabindex="-1">常见权限 <a class="header-anchor" href="#常见权限" aria-label="Permalink to &quot;常见权限&quot;">​</a></h3><table tabindex="0"><thead><tr><th>权限</th><th>说明</th></tr></thead><tbody><tr><td><strong>查看频道</strong></td><td>是否能看到该频道</td></tr><tr><td><strong>发送消息</strong></td><td>是否能在频道发言</td></tr><tr><td><strong>编辑消息</strong></td><td>是否能编辑自己的消息</td></tr><tr><td><strong>删除消息</strong></td><td>是否能删除消息</td></tr><tr><td><strong>管理成员</strong></td><td>是否能管理频道成员</td></tr><tr><td><strong>管理频道</strong></td><td>是否能编辑频道设置</td></tr><tr><td><strong>管理世界</strong></td><td>是否能编辑世界设置</td></tr><tr><td><strong>生成邀请</strong></td><td>是否能创建邀请链接</td></tr></tbody></table><h3 id="角色类型" tabindex="-1">角色类型 <a class="header-anchor" href="#角色类型" aria-label="Permalink to &quot;角色类型&quot;">​</a></h3><table tabindex="0"><thead><tr><th>角色</th><th>权限范围</th><th>说明</th></tr></thead><tbody><tr><td><strong>系统管理员</strong></td><td>全局</td><td>管理所有世界、用户、Bot</td></tr><tr><td><strong>世界管理员</strong></td><td>单个世界</td><td>管理该世界的所有设置</td></tr><tr><td><strong>频道管理员</strong></td><td>单个频道</td><td>管理该频道的设置和成员</td></tr><tr><td><strong>普通成员</strong></td><td>有限</td><td>按配置的权限操作</td></tr></tbody></table><hr><h2 id="消息类型" tabindex="-1">消息类型 <a class="header-anchor" href="#消息类型" aria-label="Permalink to &quot;消息类型&quot;">​</a></h2><h3 id="基础消息" tabindex="-1">基础消息 <a class="header-anchor" href="#基础消息" aria-label="Permalink to &quot;基础消息&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>说明</th></tr></thead><tbody><tr><td><strong>文本消息</strong></td><td>普通文字消息，支持富文本格式</td></tr><tr><td><strong>附件消息</strong></td><td>包含文件附件的消息</td></tr><tr><td><strong>图库消息</strong></td><td>引用图库图片的消息</td></tr><tr><td><strong>音频消息</strong></td><td>包含音频的消息</td></tr></tbody></table><h3 id="特殊标记" tabindex="-1">特殊标记 <a class="header-anchor" href="#特殊标记" aria-label="Permalink to &quot;特殊标记&quot;">​</a></h3><p>SealChat 支持消息的特殊标记，用于区分不同性质的发言：</p><table tabindex="0"><thead><tr><th>标记</th><th>全称</th><th>说明</th><th>显示效果</th></tr></thead><tbody><tr><td><strong>IC</strong></td><td>In Character</td><td>角色扮演中的发言</td><td>正常显示</td></tr><tr><td><strong>OOC</strong></td><td>Out of Character</td><td>跳出角色的发言</td><td>斜体/括号包围</td></tr></tbody></table><h3 id="ooc-ic-标记使用" tabindex="-1">OOC/IC 标记使用 <a class="header-anchor" href="#ooc-ic-标记使用" aria-label="Permalink to &quot;OOC/IC 标记使用&quot;">​</a></h3><p><strong>什么时候用 OOC？</strong></p><ul><li>讨论规则</li><li>询问主持人</li><li>玩家间的闲聊</li><li>对游戏的评论</li></ul><p><strong>什么时候用 IC？</strong></p><ul><li>角色对话</li><li>角色行动描述</li><li>角色内心独白</li></ul><p><strong>如何标记？</strong></p><ul><li>在消息输入框中选择 OOC/IC 模式</li><li>或使用快捷语法：<code>((这是 OOC 内容))</code></li></ul><h3 id="悄悄话" tabindex="-1">悄悄话 <a class="header-anchor" href="#悄悄话" aria-label="Permalink to &quot;悄悄话&quot;">​</a></h3><p>悄悄话是只有特定用户能看到的消息，其他人无法查看。</p><p><strong>使用场景</strong>：</p><ul><li>主持人给某个玩家的私密信息</li><li>角色间的私语</li><li>隐骰结果</li></ul><p><strong>如何发送</strong>：</p><ol><li>点击消息输入框的&quot;悄悄话&quot;按钮</li><li>选择能看到这条消息的用户</li><li>输入内容并发送</li></ol><hr><h2 id="术语表" tabindex="-1">术语表 <a class="header-anchor" href="#术语表" aria-label="Permalink to &quot;术语表&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>说明</th></tr></thead><tbody><tr><td>世界</td><td>World</td><td>最大的组织单位</td></tr><tr><td>频道</td><td>Channel</td><td>世界内的聊天空间</td></tr><tr><td>身份卡</td><td>Identity Card</td><td>用户在频道中的虚拟身份</td></tr><tr><td>OOC</td><td>Out of Character</td><td>跳出角色，玩家本人发言</td></tr><tr><td>IC</td><td>In Character</td><td>角色扮演中，角色发言</td></tr><tr><td>悄悄话</td><td>Whisper</td><td>只有特定人能看到的消息</td></tr><tr><td>隐骰</td><td>Hidden Roll</td><td>只有投骰人和主持人能看到的骰子结果</td></tr><tr><td>骰子宏</td><td>Dice Macro</td><td>预定义的骰子表达式</td></tr><tr><td>iForm</td><td>Inline Form</td><td>嵌入频道的交互表单</td></tr><tr><td>便签</td><td>Sticky Note</td><td>频道顶部的公告区域</td></tr><tr><td>导出</td><td>Export</td><td>将聊天记录导出为文件</td></tr><tr><td>Bot</td><td>Bot</td><td>自动化机器人账号</td></tr></tbody></table>`,88)])])}const u=a(s,[["render",e]]);export{c as __pageData,u as default};
