import{_ as l,o as i,c as t,ag as e}from"./chunks/framework.9PtxzSVj.js";const b=JSON.parse('{"title":"附件、图库与音频","description":"文件上传、图库管理与音频素材库","frontmatter":{"title":"附件、图库与音频","description":"文件上传、图库管理与音频素材库"},"headers":[],"relativePath":"guide/features/assets.md","filePath":"guide/features/assets.md","lastUpdated":1770390275000}'),r={name:"guide/features/assets.md"};function o(h,a,d,n,s,u){return i(),t("div",null,[...a[0]||(a[0]=[e('<h1 id="附件、图库与音频" tabindex="-1">附件、图库与音频 <a class="header-anchor" href="#附件、图库与音频" aria-label="Permalink to &quot;附件、图库与音频&quot;">​</a></h1><h2 id="上传附件" tabindex="-1">上传附件 <a class="header-anchor" href="#上传附件" aria-label="Permalink to &quot;上传附件&quot;">​</a></h2><h3 id="方式一-点击上传" tabindex="-1">方式一：点击上传 <a class="header-anchor" href="#方式一-点击上传" aria-label="Permalink to &quot;方式一：点击上传&quot;">​</a></h3><ol><li>点击输入框的附件按钮（回形针图标）</li><li>选择要上传的文件</li><li>等待上传完成</li><li>发送消息</li></ol><h3 id="方式二-拖拽上传" tabindex="-1">方式二：拖拽上传 <a class="header-anchor" href="#方式二-拖拽上传" aria-label="Permalink to &quot;方式二：拖拽上传&quot;">​</a></h3><ol><li>将文件拖拽到聊天区域</li><li>松开鼠标上传</li><li>发送消息</li></ol><h3 id="方式三-粘贴上传" tabindex="-1">方式三：粘贴上传 <a class="header-anchor" href="#方式三-粘贴上传" aria-label="Permalink to &quot;方式三：粘贴上传&quot;">​</a></h3><ol><li>复制图片（如截图）</li><li>在输入框按 <code>Ctrl + V</code></li><li>发送消息</li></ol><h2 id="支持的文件类型" tabindex="-1">支持的文件类型 <a class="header-anchor" href="#支持的文件类型" aria-label="Permalink to &quot;支持的文件类型&quot;">​</a></h2><table tabindex="0"><thead><tr><th>类型</th><th>格式</th><th>说明</th></tr></thead><tbody><tr><td>图片</td><td>JPG, PNG, GIF, WebP</td><td>自动生成缩略图</td></tr><tr><td>音频</td><td>MP3, OGG, WAV, AAC, FLAC</td><td>可在线播放</td></tr><tr><td>视频</td><td>MP4, WebM</td><td>视平台配置</td></tr><tr><td>文档</td><td>PDF, DOC, TXT 等</td><td>下载查看</td></tr></tbody></table><h2 id="文件大小限制" tabindex="-1">文件大小限制 <a class="header-anchor" href="#文件大小限制" aria-label="Permalink to &quot;文件大小限制&quot;">​</a></h2><ul><li>默认单个文件限制：64 MB</li><li>可在配置文件中调整</li></ul><h2 id="查看附件" tabindex="-1">查看附件 <a class="header-anchor" href="#查看附件" aria-label="Permalink to &quot;查看附件&quot;">​</a></h2><p><strong>图片</strong>：</p><ul><li>点击缩略图查看大图</li><li>支持放大、缩小、旋转</li></ul><p><strong>音频</strong>：</p><ul><li>直接在消息中播放</li><li>支持进度控制</li></ul><p><strong>其他文件</strong>：</p><ul><li>点击下载到本地</li></ul><h2 id="频道图片浏览器" tabindex="-1">频道图片浏览器 <a class="header-anchor" href="#频道图片浏览器" aria-label="Permalink to &quot;频道图片浏览器&quot;">​</a></h2><p>频道图片浏览器用于集中查看频道内图片：</p><ul><li>按时间流浏览图片列表</li><li>支持旋转、缩放与全屏查看</li><li>新图片发送后会自动刷新列表</li></ul><h2 id="图库管理" tabindex="-1">图库管理 <a class="header-anchor" href="#图库管理" aria-label="Permalink to &quot;图库管理&quot;">​</a></h2><p>图库用于集中管理和复用图片资源。</p><h3 id="访问图库" tabindex="-1">访问图库 <a class="header-anchor" href="#访问图库" aria-label="Permalink to &quot;访问图库&quot;">​</a></h3><ol><li>点击输入框的图库按钮（图片图标）</li><li>或进入用户设置 → 图库管理</li></ol><h3 id="上传到图库" tabindex="-1">上传到图库 <a class="header-anchor" href="#上传到图库" aria-label="Permalink to &quot;上传到图库&quot;">​</a></h3><ol><li>打开图库</li><li>点击&quot;上传&quot;按钮</li><li>选择图片文件</li><li>可以添加标签和描述</li></ol><h3 id="在消息中使用-图库" tabindex="-1">在消息中使用（图库） <a class="header-anchor" href="#在消息中使用-图库" aria-label="Permalink to &quot;在消息中使用（图库）&quot;">​</a></h3><ol><li>点击图库按钮</li><li>浏览或搜索图片</li><li>选择要插入的图片</li><li>发送消息</li></ol><h3 id="图库配额" tabindex="-1">图库配额 <a class="header-anchor" href="#图库配额" aria-label="Permalink to &quot;图库配额&quot;">​</a></h3><ul><li>每个用户有图库配额限制</li><li>默认 100 MB</li><li>可在配置中调整</li></ul><h3 id="图库分类" tabindex="-1">图库分类 <a class="header-anchor" href="#图库分类" aria-label="Permalink to &quot;图库分类&quot;">​</a></h3><ul><li>支持按标签分类</li><li>支持按上传时间排序</li><li>支持搜索图片名称</li></ul><h2 id="音频管理" tabindex="-1">音频管理 <a class="header-anchor" href="#音频管理" aria-label="Permalink to &quot;音频管理&quot;">​</a></h2><p>音频库用于管理音频素材，如背景音乐、音效等。</p><h3 id="访问音频库" tabindex="-1">访问音频库 <a class="header-anchor" href="#访问音频库" aria-label="Permalink to &quot;访问音频库&quot;">​</a></h3><ol><li>进入用户设置 → 音频库</li><li>或在频道中点击音频按钮</li></ol><h3 id="上传音频" tabindex="-1">上传音频 <a class="header-anchor" href="#上传音频" aria-label="Permalink to &quot;上传音频&quot;">​</a></h3><ol><li>打开音频库</li><li>点击&quot;上传&quot;</li><li>选择音频文件</li><li>填写名称和描述</li></ol><p><strong>支持格式</strong>：</p><ul><li>MP3, OGG, WAV, WebM, AAC, FLAC</li></ul><p><strong>自动转码</strong>：</p><ul><li>上传后会自动转码为统一格式</li><li>可配置目标比特率</li></ul><h3 id="在消息中使用-音频" tabindex="-1">在消息中使用（音频） <a class="header-anchor" href="#在消息中使用-音频" aria-label="Permalink to &quot;在消息中使用（音频）&quot;">​</a></h3><ol><li>点击音频按钮</li><li>选择音频</li><li>发送消息</li></ol><h3 id="音频播放" tabindex="-1">音频播放 <a class="header-anchor" href="#音频播放" aria-label="Permalink to &quot;音频播放&quot;">​</a></h3><ul><li>消息中的音频可直接播放</li><li>支持暂停、进度调整、音量控制</li></ul>',48)])])}const q=l(r,[["render",o]]);export{b as __pageData,q as default};
