import{_ as a,o as e,c as n,ag as i}from"./chunks/framework.9PtxzSVj.js";const u=JSON.parse('{"title":"骰子与跑团工具","description":"骰子表达式、骰子宏、托盘与隐骰","frontmatter":{"title":"骰子与跑团工具","description":"骰子表达式、骰子宏、托盘与隐骰"},"headers":[],"relativePath":"guide/features/dice.md","filePath":"guide/features/dice.md","lastUpdated":1770390275000}'),t={name:"guide/features/dice.md"};function l(p,s,d,r,o,c){return e(),n("div",null,[...s[0]||(s[0]=[i(`<h1 id="骰子与跑团工具" tabindex="-1">骰子与跑团工具 <a class="header-anchor" href="#骰子与跑团工具" aria-label="Permalink to &quot;骰子与跑团工具&quot;">​</a></h1><p>SealChat 内置强大的骰子引擎，支持复杂的骰子表达式。</p><h2 id="基础骰子命令" tabindex="-1">基础骰子命令 <a class="header-anchor" href="#基础骰子命令" aria-label="Permalink to &quot;基础骰子命令&quot;">​</a></h2><p>在消息中输入骰子命令，发送后自动求值：</p><table tabindex="0"><thead><tr><th>命令</th><th>说明</th><th>示例结果</th></tr></thead><tbody><tr><td><code>.r d20</code></td><td>投一个 20 面骰</td><td>掷骰: d20 = 15</td></tr><tr><td><code>.r 2d6</code></td><td>投两个 6 面骰</td><td>掷骰: 2d6 = 7 (3+4)</td></tr><tr><td><code>.r d20+5</code></td><td>投 d20 加 5</td><td>掷骰: d20+5 = 18 (13+5)</td></tr><tr><td><code>.r 3d6k2</code></td><td>投 3d6 保留最高 2 个</td><td>掷骰: 3d6k2 = 9 (5+4)</td></tr><tr><td><code>.r 4d6kl3</code></td><td>投 4d6 保留最低 3 个</td><td>掷骰: 4d6kl3 = 6 (1+2+3)</td></tr></tbody></table><h2 id="高级骰子表达式" tabindex="-1">高级骰子表达式 <a class="header-anchor" href="#高级骰子表达式" aria-label="Permalink to &quot;高级骰子表达式&quot;">​</a></h2><p><strong>算术运算</strong>：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.r (d20+5)*2</span></span>
<span class="line"><span>.r d20+d6+3</span></span>
<span class="line"><span>.r 2d6+1d8-2</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p><strong>保留/丢弃规则</strong>：</p><ul><li><code>k</code> 或 <code>kh</code>：保留最高（Keep Highest）</li><li><code>kl</code>：保留最低（Keep Lowest）</li><li><code>d</code> 或 <code>dl</code>：丢弃最低（Drop Lowest）</li><li><code>dh</code>：丢弃最高（Drop Highest）</li></ul><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.r 4d6dl1    // 投4d6，丢弃最低1个（常用于D&amp;D属性生成）</span></span>
<span class="line"><span>.r 2d20kh1   // 投2d20，保留最高1个（优势骰）</span></span>
<span class="line"><span>.r 2d20kl1   // 投2d20，保留最低1个（劣势骰）</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p><strong>重投规则</strong>：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.r 4d6r1     // 投4d6，1点重投</span></span>
<span class="line"><span>.r 4d6ro1    // 投4d6，1点重投一次</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p><strong>爆炸骰</strong>：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.r 4d6!      // 投4d6，最大值爆炸（继续投）</span></span>
<span class="line"><span>.r 4d6!&gt;5    // 投4d6，大于5爆炸</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="骰子宏" tabindex="-1">骰子宏 <a class="header-anchor" href="#骰子宏" aria-label="Permalink to &quot;骰子宏&quot;">​</a></h2><p>骰子宏是预定义的骰子表达式，方便快速使用。</p><p><strong>查看可用宏</strong>：</p><ul><li>在频道设置中查看已配置的骰子宏</li></ul><p><strong>使用宏</strong>：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.r 宏名称</span></span>
<span class="line"><span>.r 攻击骰</span></span>
<span class="line"><span>.r 伤害骰</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p><strong>创建宏（管理员）</strong>：</p><ol><li>进入频道设置</li><li>选择&quot;骰子宏管理&quot;</li><li>点击&quot;创建宏&quot;</li><li>输入宏名称和表达式</li></ol><p><strong>宏示例</strong>：</p><div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">攻击骰</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">d20+7</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">       # 攻击检定</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">伤害骰</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">2d6+4</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">       # 伤害骰</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">豁免骰</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">d20+3</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">       # 豁免检定</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">先攻骰</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">d20+2</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">       # 先攻检定</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h2 id="骰子托盘" tabindex="-1">骰子托盘 <a class="header-anchor" href="#骰子托盘" aria-label="Permalink to &quot;骰子托盘&quot;">​</a></h2><p>骰子托盘提供快捷掷骰与历史记录管理：</p><ul><li>一键选择常见面数（d4/d6/d8/d10/d12/d20/d100）</li><li>支持数量、修正值与原因说明</li><li>记录掷骰历史，支持收藏常用表达式</li><li>支持对历史表达式进行微调</li></ul><h2 id="隐骰" tabindex="-1">隐骰 <a class="header-anchor" href="#隐骰" aria-label="Permalink to &quot;隐骰&quot;">​</a></h2><p>隐骰的结果只有投骰人和主持人能看到。</p><p><strong>使用方法</strong>：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>.rh d20+5    // 隐骰</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><strong>显示效果</strong>：</p><ul><li>其他玩家只能看到&quot;某某进行了一次隐骰&quot;</li><li>投骰人和主持人可以看到完整结果</li></ul><p><strong>使用场景</strong>：</p><ul><li>主持人的 NPC 检定</li><li>需要保密的结果</li><li>陷阱/感知检定</li></ul>`,36)])])}const b=a(t,[["render",l]]);export{u as __pageData,b as default};
