import{_ as t,o as i,c as a,ag as e}from"./chunks/framework.9PtxzSVj.js";const u=JSON.parse('{"title":"消息与聊天","description":"发送、编辑、悄悄话、表情与消息归档","frontmatter":{"title":"消息与聊天","description":"发送、编辑、悄悄话、表情与消息归档"},"headers":[],"relativePath":"guide/features/messaging.md","filePath":"guide/features/messaging.md","lastUpdated":1770390275000}'),o={name:"guide/features/messaging.md"};function s(r,l,d,n,c,p){return i(),a("div",null,[...l[0]||(l[0]=[e('<h1 id="消息与聊天" tabindex="-1">消息与聊天 <a class="header-anchor" href="#消息与聊天" aria-label="Permalink to &quot;消息与聊天&quot;">​</a></h1><h2 id="发送消息" tabindex="-1">发送消息 <a class="header-anchor" href="#发送消息" aria-label="Permalink to &quot;发送消息&quot;">​</a></h2><p><strong>基础发送</strong>：</p><ol><li>在输入框中输入内容</li><li>按 <code>Enter</code> 发送</li></ol><p><strong>换行</strong>：</p><ul><li>按 <code>Shift + Enter</code> 在消息中换行</li></ul><p><strong>富文本格式</strong>：</p><table tabindex="0"><thead><tr><th>格式</th><th>快捷键</th><th>Markdown 语法</th></tr></thead><tbody><tr><td><strong>粗体</strong></td><td><code>Ctrl + B</code></td><td><code>**文字**</code></td></tr><tr><td><em>斜体</em></td><td><code>Ctrl + I</code></td><td><code>*文字*</code></td></tr><tr><td><s>删除线</s></td><td>-</td><td><code>~~文字~~</code></td></tr><tr><td><code>代码</code></td><td><code>Ctrl + E</code></td><td><code>`代码`</code></td></tr><tr><td>链接</td><td><code>Ctrl + K</code></td><td><code>[文字](URL)</code></td></tr></tbody></table><p><strong>代码块</strong>：</p><p>输入三个反引号，然后输入语言名称：</p><div class="language-javascript vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">javascript</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">console.</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">log</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;Hello&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">);</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="编辑与撤回" tabindex="-1">编辑与撤回 <a class="header-anchor" href="#编辑与撤回" aria-label="Permalink to &quot;编辑与撤回&quot;">​</a></h2><p><strong>编辑消息</strong>：</p><ol><li>鼠标悬停在自己发送的消息上</li><li>点击编辑图标（铅笔）</li><li>修改内容后按 <code>Enter</code> 保存</li><li>按 <code>Escape</code> 取消编辑</li></ol><div class="info custom-block"><p class="custom-block-title">编辑历史</p><p>被编辑的消息会显示&quot;已编辑&quot;标记，点击可查看编辑历史。</p></div><p><strong>撤回消息</strong>：</p><ol><li>鼠标悬停在自己发送的消息上</li><li>点击删除图标（垃圾桶）</li><li>确认撤回</li></ol><div class="warning custom-block"><p class="custom-block-title">注意</p><ul><li>撤回后其他人无法看到原内容</li><li>管理员可以恢复撤回的消息</li></ul></div><h2 id="ooc-ic-标记" tabindex="-1">OOC/IC 标记 <a class="header-anchor" href="#ooc-ic-标记" aria-label="Permalink to &quot;OOC/IC 标记&quot;">​</a></h2><p><strong>通过界面切换</strong>：</p><ol><li>在输入框左侧找到 OOC/IC 切换按钮</li><li>选择当前发言模式</li><li>发送的消息会带有相应标记</li></ol><p><strong>通过语法标记</strong>：</p><ul><li>OOC 语法：<code>((内容))</code> 或 <code>（（内容））</code></li><li>发送后自动识别为 OOC 消息</li></ul><p><strong>显示效果</strong>：</p><ul><li>IC 消息：正常显示</li><li>OOC 消息：使用不同颜色或样式标识</li></ul><h2 id="悄悄话" tabindex="-1">悄悄话 <a class="header-anchor" href="#悄悄话" aria-label="Permalink to &quot;悄悄话&quot;">​</a></h2><p><strong>发送悄悄话</strong>：</p><ol><li>点击输入框的悄悄话按钮（眼睛图标）</li><li>选择可以看到这条消息的用户</li><li>输入内容并发送</li></ol><p><strong>悄悄话显示</strong>：</p><ul><li>只有发送者和被选中的用户能看到</li><li>消息会有特殊标记表示这是悄悄话</li></ul><p><strong>使用场景</strong>：</p><ul><li>主持人给玩家的私密信息</li><li>角色间的私语对话</li><li>不公开的骰子结果</li></ul><h2 id="消息反应" tabindex="-1">消息反应 <a class="header-anchor" href="#消息反应" aria-label="Permalink to &quot;消息反应&quot;">​</a></h2><ol><li>鼠标悬停在消息上</li><li>点击表情图标</li><li>选择要添加的表情反应</li></ol><p><strong>移除反应</strong>：</p><ul><li>点击已添加的反应即可移除</li></ul><h2 id="表情与收藏" tabindex="-1">表情与收藏 <a class="header-anchor" href="#表情与收藏" aria-label="Permalink to &quot;表情与收藏&quot;">​</a></h2><p><strong>打开表情面板</strong>：</p><ul><li>点击输入框旁的表情按钮</li></ul><p><strong>收藏表情</strong>：</p><ul><li>右键消息中的图片，添加到表情收藏</li><li>常用表情会自动进入最近使用列表</li></ul><p><strong>自定义表情</strong>：</p><ul><li>在个人设置中管理自定义表情</li><li>支持添加、重命名与删除</li></ul><h2 id="输入历史与撤销" tabindex="-1">输入历史与撤销 <a class="header-anchor" href="#输入历史与撤销" aria-label="Permalink to &quot;输入历史与撤销&quot;">​</a></h2><ul><li>支持输入内容的撤销/重做</li><li>富文本与混合输入模式下均可使用</li><li>长段编辑会自动合并为更合理的历史步长</li></ul><h2 id="消息归档" tabindex="-1">消息归档 <a class="header-anchor" href="#消息归档" aria-label="Permalink to &quot;消息归档&quot;">​</a></h2><p>归档用于收藏重要消息，便于回看与整理。</p><p><strong>归档消息</strong>：</p><ol><li>右键点击消息</li><li>选择&quot;归档&quot;</li></ol><p><strong>查看归档</strong>：</p><ul><li>点击工具栏中的归档图标</li><li>支持搜索、恢复与删除</li></ul>',51)])])}const g=t(o,[["render",s]]);export{u as __pageData,g as default};
