import{_ as a,o as e,c as d,ag as r}from"./chunks/framework.9PtxzSVj.js";const b=JSON.parse('{"title":"搜索与导出","description":"历史搜索筛选与聊天记录导出","frontmatter":{"title":"搜索与导出","description":"历史搜索筛选与聊天记录导出"},"headers":[],"relativePath":"guide/features/search-export.md","filePath":"guide/features/search-export.md","lastUpdated":1770390275000}'),l={name:"guide/features/search-export.md"};function o(i,t,h,s,n,c){return e(),d("div",null,[...t[0]||(t[0]=[r('<h1 id="搜索与导出" tabindex="-1">搜索与导出 <a class="header-anchor" href="#搜索与导出" aria-label="Permalink to &quot;搜索与导出&quot;">​</a></h1><h2 id="消息搜索" tabindex="-1">消息搜索 <a class="header-anchor" href="#消息搜索" aria-label="Permalink to &quot;消息搜索&quot;">​</a></h2><h3 id="基础搜索" tabindex="-1">基础搜索 <a class="header-anchor" href="#基础搜索" aria-label="Permalink to &quot;基础搜索&quot;">​</a></h3><ol><li>点击频道顶部的搜索图标</li><li>输入关键词</li><li>按 <code>Enter</code> 搜索</li></ol><h3 id="高级搜索" tabindex="-1">高级搜索 <a class="header-anchor" href="#高级搜索" aria-label="Permalink to &quot;高级搜索&quot;">​</a></h3><p>支持多种搜索条件：</p><table tabindex="0"><thead><tr><th>条件</th><th>语法</th><th>说明</th></tr></thead><tbody><tr><td>关键词</td><td><code>魔法</code></td><td>搜索包含&quot;魔法&quot;的消息</td></tr><tr><td>发送者</td><td><code>from:用户名</code></td><td>搜索某用户的消息</td></tr><tr><td>日期</td><td><code>after:2024-01-01</code></td><td>搜索某日期之后的消息</td></tr><tr><td>日期</td><td><code>before:2024-12-31</code></td><td>搜索某日期之前的消息</td></tr><tr><td>频道</td><td><code>in:频道名</code></td><td>搜索特定频道</td></tr></tbody></table><p><strong>组合搜索</strong>：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>from:艾露莎 魔法 after:2024-01-01</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="搜索结果" tabindex="-1">搜索结果 <a class="header-anchor" href="#搜索结果" aria-label="Permalink to &quot;搜索结果&quot;">​</a></h3><ul><li>结果按时间排序</li><li>显示匹配消息的上下文</li><li>点击可跳转到原消息位置</li></ul><h3 id="筛选面板" tabindex="-1">筛选面板 <a class="header-anchor" href="#筛选面板" aria-label="Permalink to &quot;筛选面板&quot;">​</a></h3><p>搜索面板提供更细粒度筛选：</p><ul><li>发言人筛选（支持多选）</li><li>时间范围筛选</li><li>是否包含场外消息</li></ul><h2 id="消息导出" tabindex="-1">消息导出 <a class="header-anchor" href="#消息导出" aria-label="Permalink to &quot;消息导出&quot;">​</a></h2><p>导出功能可以将聊天记录保存为文件，方便整理和分享。</p><h3 id="导出入口" tabindex="-1">导出入口 <a class="header-anchor" href="#导出入口" aria-label="Permalink to &quot;导出入口&quot;">​</a></h3><ol><li>进入频道设置</li><li>选择&quot;导出消息&quot;</li><li>或使用命令 <code>/export</code></li></ol><h3 id="导出格式" tabindex="-1">导出格式 <a class="header-anchor" href="#导出格式" aria-label="Permalink to &quot;导出格式&quot;">​</a></h3><table tabindex="0"><thead><tr><th>格式</th><th>说明</th><th>适用场景</th></tr></thead><tbody><tr><td><strong>纯文本</strong></td><td><code>.txt</code> 格式</td><td>快速备份、二次处理</td></tr><tr><td><strong>HTML</strong></td><td>网页格式，保留样式</td><td>分享、存档、打印</td></tr><tr><td><strong>海豹染色器</strong></td><td>BBCode/Docx</td><td>跑团记录整理与排版</td></tr></tbody></table><h3 id="导出选项" tabindex="-1">导出选项 <a class="header-anchor" href="#导出选项" aria-label="Permalink to &quot;导出选项&quot;">​</a></h3><table tabindex="0"><thead><tr><th>选项</th><th>说明</th></tr></thead><tbody><tr><td>时间范围</td><td>选择要导出的消息时间段</td></tr><tr><td>包含 OOC</td><td>是否包含 OOC 消息</td></tr><tr><td>包含归档</td><td>是否包含已归档消息</td></tr><tr><td>隐藏时间</td><td>导出时移除时间戳</td></tr><tr><td>合并消息</td><td>合并同一用户的连续消息</td></tr></tbody></table><h3 id="导出流程" tabindex="-1">导出流程 <a class="header-anchor" href="#导出流程" aria-label="Permalink to &quot;导出流程&quot;">​</a></h3><ol><li>选择导出参数</li><li>点击&quot;开始导出&quot;</li><li>等待后台处理</li><li>处理完成后下载文件</li></ol><div class="tip custom-block"><p class="custom-block-title">后台处理</p><p>大量消息的导出可能需要一些时间。导出任务在后台执行，不影响正常使用。导出完成后会收到通知。</p></div><h3 id="html-导出查看器" tabindex="-1">HTML 导出查看器 <a class="header-anchor" href="#html-导出查看器" aria-label="Permalink to &quot;HTML 导出查看器&quot;">​</a></h3><p>导出的 HTML 文件包含独立的查看器：</p><ul><li>无需联网即可查看</li><li>保留原始格式和样式</li><li>支持搜索和导航</li></ul><h3 id="海豹染色器上传-可选" tabindex="-1">海豹染色器上传（可选） <a class="header-anchor" href="#海豹染色器上传-可选" aria-label="Permalink to &quot;海豹染色器上传（可选）&quot;">​</a></h3><p>如管理员启用云端上传，导出为海豹染色器格式时可自动上传生成 BBCode/Docx。</p>',30)])])}const p=a(l,[["render",o]]);export{b as __pageData,p as default};
