import{_ as a,o as e,c as l,ag as i}from"./chunks/framework.9PtxzSVj.js";const b=JSON.parse('{"title":"显示与通知设置","description":"主题、快捷键与通知策略","frontmatter":{"title":"显示与通知设置","description":"主题、快捷键与通知策略"},"headers":[],"relativePath":"guide/features/settings.md","filePath":"guide/features/settings.md","lastUpdated":1770390275000}'),d={name:"guide/features/settings.md"};function r(o,t,h,n,c,s){return e(),l("div",null,[...t[0]||(t[0]=[i('<h1 id="显示与通知设置" tabindex="-1">显示与通知设置 <a class="header-anchor" href="#显示与通知设置" aria-label="Permalink to &quot;显示与通知设置&quot;">​</a></h1><h2 id="显示设置与主题" tabindex="-1">显示设置与主题 <a class="header-anchor" href="#显示设置与主题" aria-label="Permalink to &quot;显示设置与主题&quot;">​</a></h2><h3 id="布局与配色" tabindex="-1">布局与配色 <a class="header-anchor" href="#布局与配色" aria-label="Permalink to &quot;布局与配色&quot;">​</a></h3><ul><li>日间/夜间主题切换</li><li>气泡/紧凑布局切换</li><li>时间戳显示格式可选</li></ul><h3 id="自定义主题" tabindex="-1">自定义主题 <a class="header-anchor" href="#自定义主题" aria-label="Permalink to &quot;自定义主题&quot;">​</a></h3><ul><li>颜色字段可视化编辑</li><li>支持主题导入/导出</li><li>适合长期跑团或风格化场景</li></ul><h3 id="工具栏快捷键" tabindex="-1">工具栏快捷键 <a class="header-anchor" href="#工具栏快捷键" aria-label="Permalink to &quot;工具栏快捷键&quot;">​</a></h3><p>工具栏支持为常用动作设置快捷键：</p><ul><li>OOC/IC 切换</li><li>悄悄话面板</li><li>上传图片与表情面板</li><li>历史记录与骰子托盘</li></ul><h3 id="输入体验" tabindex="-1">输入体验 <a class="header-anchor" href="#输入体验" aria-label="Permalink to &quot;输入体验&quot;">​</a></h3><ul><li>可设置输入框宽度</li><li>工具栏按钮支持快捷键</li></ul><h2 id="通知设置" tabindex="-1">通知设置 <a class="header-anchor" href="#通知设置" aria-label="Permalink to &quot;通知设置&quot;">​</a></h2><h3 id="邮件通知" tabindex="-1">邮件通知 <a class="header-anchor" href="#邮件通知" aria-label="Permalink to &quot;邮件通知&quot;">​</a></h3><p>如果服务器配置了邮件功能：</p><ol><li>进入用户设置 → 通知</li><li>填写接收通知的邮箱</li><li>配置通知频率</li></ol><p><strong>通知类型</strong>：</p><ul><li>未读消息通知</li><li>@提及通知</li><li>好友请求通知</li></ul><h3 id="频道级邮件通知" tabindex="-1">频道级邮件通知 <a class="header-anchor" href="#频道级邮件通知" aria-label="Permalink to &quot;频道级邮件通知&quot;">​</a></h3><p>管理员可以为指定频道配置邮件通知策略，适用于重要公告频道。</p><p><strong>可配置项</strong>：</p><ul><li>接收邮箱与推送延迟</li><li>自定义 SMTP（可选）</li><li>发送测试邮件验证配置</li></ul><h3 id="通知延迟" tabindex="-1">通知延迟 <a class="header-anchor" href="#通知延迟" aria-label="Permalink to &quot;通知延迟&quot;">​</a></h3><ul><li>可配置最小延迟（避免频繁打扰）</li><li>可配置每小时最大通知数</li></ul><h3 id="免打扰" tabindex="-1">免打扰 <a class="header-anchor" href="#免打扰" aria-label="Permalink to &quot;免打扰&quot;">​</a></h3><ul><li>可以为特定频道设置免打扰</li><li>静音后不会收到该频道的通知</li></ul><h2 id="快捷键汇总" tabindex="-1">快捷键汇总 <a class="header-anchor" href="#快捷键汇总" aria-label="Permalink to &quot;快捷键汇总&quot;">​</a></h2><table tabindex="0"><thead><tr><th>快捷键</th><th>功能</th></tr></thead><tbody><tr><td><code>Enter</code></td><td>发送消息</td></tr><tr><td><code>Shift + Enter</code></td><td>消息内换行</td></tr><tr><td><code>Ctrl + B</code></td><td>粗体</td></tr><tr><td><code>Ctrl + I</code></td><td>斜体</td></tr><tr><td><code>Ctrl + K</code></td><td>插入链接</td></tr><tr><td><code>Ctrl + E</code></td><td>行内代码</td></tr><tr><td><code>Ctrl + Z</code></td><td>撤销</td></tr><tr><td><code>Ctrl + Y</code></td><td>重做</td></tr><tr><td><code>Escape</code></td><td>取消编辑</td></tr><tr><td><code>↑</code> (输入框为空)</td><td>编辑上一条消息</td></tr></tbody></table>',27)])])}const f=a(d,[["render",r]]);export{b as __pageData,f as default};
