import{_ as a,o as i,c as e,ag as r}from"./chunks/framework.9PtxzSVj.js";const q=JSON.parse('{"title":"协作工具","description":"iForm 表单、术语库与便签","frontmatter":{"title":"协作工具","description":"iForm 表单、术语库与便签"},"headers":[],"relativePath":"guide/features/tools.md","filePath":"guide/features/tools.md","lastUpdated":1770047908000}'),o={name:"guide/features/tools.md"};function t(h,l,u,d,n,s){return i(),e("div",null,[...l[0]||(l[0]=[r('<h1 id="协作工具" tabindex="-1">协作工具 <a class="header-anchor" href="#协作工具" aria-label="Permalink to &quot;协作工具&quot;">​</a></h1><h2 id="iform-表单" tabindex="-1">iForm 表单 <a class="header-anchor" href="#iform-表单" aria-label="Permalink to &quot;iForm 表单&quot;">​</a></h2><p>iForm 是嵌入频道的交互表单，用于收集信息。</p><h3 id="查看表单" tabindex="-1">查看表单 <a class="header-anchor" href="#查看表单" aria-label="Permalink to &quot;查看表单&quot;">​</a></h3><ul><li>表单显示在频道顶部或消息流中</li><li>点击可以填写和提交</li></ul><h3 id="创建表单-管理员" tabindex="-1">创建表单（管理员） <a class="header-anchor" href="#创建表单-管理员" aria-label="Permalink to &quot;创建表单（管理员）&quot;">​</a></h3><ol><li>进入频道设置</li><li>选择&quot;iForm 管理&quot;</li><li>点击&quot;创建表单&quot;</li><li>添加字段： <ul><li>文本框</li><li>单选</li><li>多选</li><li>日期</li><li>数字</li></ul></li></ol><h3 id="展示方式" tabindex="-1">展示方式 <a class="header-anchor" href="#展示方式" aria-label="Permalink to &quot;展示方式&quot;">​</a></h3><ul><li>面板嵌入：固定在频道顶部</li><li>抽屉模式：侧边栏打开</li><li>浮窗模式：可拖拽到任意位置</li><li>支持弹出新窗口查看</li></ul><h3 id="表单用途" tabindex="-1">表单用途 <a class="header-anchor" href="#表单用途" aria-label="Permalink to &quot;表单用途&quot;">​</a></h3><ul><li>角色信息收集</li><li>投票调查</li><li>签到统计</li><li>规则确认</li></ul><h2 id="术语与关键词" tabindex="-1">术语与关键词 <a class="header-anchor" href="#术语与关键词" aria-label="Permalink to &quot;术语与关键词&quot;">​</a></h2><p>术语功能可对频道消息进行关键词高亮与解释。</p><h3 id="创建术语-管理员" tabindex="-1">创建术语（管理员） <a class="header-anchor" href="#创建术语-管理员" aria-label="Permalink to &quot;创建术语（管理员）&quot;">​</a></h3><ol><li>进入世界设置 → 术语管理</li><li>新建关键词与描述</li><li>可设置分类、别名与匹配模式（普通/正则）</li></ol><h3 id="展示模式" tabindex="-1">展示模式 <a class="header-anchor" href="#展示模式" aria-label="Permalink to &quot;展示模式&quot;">​</a></h3><ul><li>标准：高亮并展示说明</li><li>精简：仅高亮关键词</li><li>继承：跟随世界默认设置</li></ul><h3 id="导入与导出" tabindex="-1">导入与导出 <a class="header-anchor" href="#导入与导出" aria-label="Permalink to &quot;导入与导出&quot;">​</a></h3><ul><li>支持 JSON 与 CSV 导入</li><li>可按分类导出备份</li></ul><h2 id="便签功能" tabindex="-1">便签功能 <a class="header-anchor" href="#便签功能" aria-label="Permalink to &quot;便签功能&quot;">​</a></h2><p>便签是频道内的轻量协作工具，可用于公告与流程辅助。</p><h3 id="查看便签" tabindex="-1">查看便签 <a class="header-anchor" href="#查看便签" aria-label="Permalink to &quot;查看便签&quot;">​</a></h3><ul><li>便签显示在频道中，可置顶或浮窗</li><li>可根据权限控制可见范围</li></ul><h3 id="编辑便签-管理员" tabindex="-1">编辑便签（管理员） <a class="header-anchor" href="#编辑便签-管理员" aria-label="Permalink to &quot;编辑便签（管理员）&quot;">​</a></h3><ol><li>点击便签区域的编辑按钮</li><li>输入或修改内容</li><li>支持富文本格式</li><li>保存</li></ol><h3 id="便签类型" tabindex="-1">便签类型 <a class="header-anchor" href="#便签类型" aria-label="Permalink to &quot;便签类型&quot;">​</a></h3><ul><li>文字便签：发布公告、规则、链接</li><li>清单便签：可勾选的任务列表</li><li>计数器：资源值追踪</li><li>滑条/计时器/时钟：进度与回合管理</li></ul><h3 id="便签用途" tabindex="-1">便签用途 <a class="header-anchor" href="#便签用途" aria-label="Permalink to &quot;便签用途&quot;">​</a></h3><ul><li>频道规则说明</li><li>重要公告</li><li>常用信息汇总</li><li>链接收集</li></ul>',29)])])}const m=a(o,[["render",t]]);export{q as __pageData,m as default};
